
public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * DATA TYPES
		 * 
		 */

		int num = 500; // integer
		long num0 = 50000000l; // we can store the large values
		short num1 = 43; // this can be stored of 2 bytes which is 16 bits
		byte num2 = 2; // here we can store the smallest value - 8 bits
		float percent = 4.6f; // float value stored as the percentages which is in the decimal values
		double percent1 = 5.6; // so if we use the double no need to write 'f' after the value and
        char name = 'A';  // char can be stored as the single letter
        
	}
}
