
public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * 
		 * ARITHMETIC 
		 * BITWISE
		 * RELATIONAL
		 * LOGICAL
		 * 
		 * 
		 * 
		 */
		 
		int m = 8;
		int n = 5;
		int add = m+n; // adding two numbers
		int sub = m-n; // difference between 2 numbers
		
		int mul = m*n; // multiplying the two numbers
		float div = (float)m/n;  // dividing the 2 numbers
		
		
		int rem = m%n; // printing the remainder 

		System.out.println(add);
		System.out.println(sub);
		System.out.println(mul);
		System.out.println(div);
		System.out.println(rem);
				
		
		
	}

}
