
public class Operators1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
/*
 * 
 * INCREMENT AND DECREMENT OPERATORS
 * 
 * pre increment, post increment
 * pre decrement, post decrement
 * 
 * decrement means  decrease the value by 1
 * increment means increase the value by 1
 * 
 * pre increment       ++a     first increase then assign                       post increment        a++     first assign then increase
 * 
 * pre decrement        --a       first decrease then assign                    post decrement        a-- n     first assign then decrease
 * 
 * 
 */
		
	int a =10;
	a++;
	a--;
	++a;
	--a;
	
	}

}
