
public class PrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * ASCII VALUES
		 * 
		 * 
		 */
		
		char c = 'A';

c = 66;
/////////////////////////////
 
/*
 * 
 * IMPLICIT AND EXPLICIT CONVERSIONS
 * 
 */
double d =54;

int num =(int)2.3; // here int doesn't support the decimal value but we can still support it by doing type casting which is known as explicit conversion

System.out.println(d); // output is 54.0 which is stored automatically as the decimal value known as the implicit conversions

System.out.println(num);

System.out.println(c); // output is B which is the ASCII value
		
	}

}
